definition.script:
pipeline {
    agent any

    stages {
        stage('clone') {
            steps {
                sshagent(['3dfc7ab3-ca16-4a72-acdd-234af2acc0d9']) {
                    sh '''
                        ssh -o StrictHostKeyChecking=no remote_user@remote_ubuntu 'bash -s << 'ENDSSH'
                        pwd
                        git clone https://github.com/spring-projects/spring-petclinic.git
                        ls spring-petclinic
                ENDSSH'
                        '''.stripIndent()
                }
            }
        }
        stage('build') {
            steps {
                sshagent(['3dfc7ab3-ca16-4a72-acdd-234af2acc0d9']) {
                    sh '''
                        ssh -o StrictHostKeyChecking=no remote_user@remote_ubuntu 'bash -s << 'ENDSSH'
                        cd spring-petclinic
                        ./mvnw package
                ENDSSH'
                        '''.stripIndent()
                }
            }
        }
        stage('deploy') {
            steps {
                sshagent(['3dfc7ab3-ca16-4a72-acdd-234af2acc0d9']) {
                    sh '''
                        ssh -o StrictHostKeyChecking=no remote_user@remote_ubuntu 'bash -s << 'ENDSSH'
                        ./restart.sh
                        echo "WEB RUNNING"
                ENDSSH'
                        '''.stripIndent()
                }
            }
        }
    }
}
